		sl.add(new Student("E", "e", new Date(34, 4, 5)));
		sl.add(new Student("F", "f", new Date(45, 5, 6)));
		sl.showAll();
		int n = sl.find(new Date(30, 6, 5));
		System.out.println(n);
		int k = sl.find("A", "name");
		System.out.println(k);
		int i = sl.find("c", "surname");
		System.out.println(i);
		sl.delete(0);
		sl.showAll();
	}
